var colors = require('colors');
var salida = null;

var terminos = {
    "dias": {
        "en": {
            "lunes"     : "Monday",
            "martes"    : "Tuesday",
            "miercoles" : "Wednesday",
            "jueves"    : "Thursday",
            "viernes"   : "Friday",
            "sabado"    : "Saturday",
            "domingo"   : "Sunday",
        },
        "br": {
            "lunes"     : "segunda-feira",
            "martes"    : "terça-feira",
            "miercoles" : "quarta-feira",
            "jueves"    : "quinta-feira",
            "viernes"   : "sexta-feira",
            "sabado"    : "sábado",
            "domingo"   : "domingo",
        }
    },
    "meses": {
        "en": {
            "enero"     : "January",
            "febrero"   : "February",
            "marzo"     : "March",
            "abril"     : "April",
            "mayo"      : "May",
            "junio"     : "June",
            "julio"     : "July",
            "agosto"    : "August",
            "septiembre": "September",
            "octubre"   : "October",
            "noviembre" : "November",
            "diciembre" : "December"
        },
        "br": {
            "enero"     : "Janeiro",
            "febrero"   : "Fevereiro",
            "marzo"     : "Março",
            "abril"     : "Abril",
            "mayo"      : "Maio",
            "junio"     : "Junho",
            "julio"     : "Julho",
            "agosto"    : "Agosto",
            "septiembre": "Setembro",
            "octubre"   : "Outubro",
            "noviembre" : "Novembro",
            "diciembre" : "Dezembro"
        }
    }
};

var traducir_dia = function (dia, idioma) {
    dia    = dia.toLowerCase();
    idioma = idioma.toLowerCase();

    if (terminos['dias'][idioma] == null)
        salida = ('El Idioma: "' + idioma + '" No existe.').red;
    else{
        if (terminos['dias'][idioma][dia] == null)
            salida = ('El Día: "' + dia  + '" No existe para el idioma "' + idioma + '"').red;
        else
            salida = dia + ' en ' + idioma.bold +' es: ' + terminos['dias'][idioma][dia].magenta;
    }
    return salida;
};

var traducir_mes = function (mes, idioma) {
    mes    = mes.toLowerCase();
    idioma = idioma.toLowerCase();

    if (terminos['meses'][idioma] == null)
        salida = ('El Idioma: "' + idioma + '" No existe.').red;
    else{
        if (terminos['meses'][idioma][mes] == null)
            salida = ('El Mes: "' + mes  + '" No existe para el idioma "' + idioma + '"').red;
        else
            salida =  mes + ' en ' + idioma.bold +' es: ' + terminos['meses'][idioma][mes].magenta;
    }
    return salida;
};

exports.traducir_dia = traducir_dia;
exports.traducir_mes = traducir_mes;